head.script:
function totalWordsFx(){
				var maxlimit = 300;
				var array = document.forms[0].elements['dissertationSummary'];
				 var words=array.value.split(/[^\w\d-'.?!]+/g); 
				 for(var i=0;i<words.length;i++){ 
				 if(!words[i]){ 
				  words.splice(i,1); 
				  i--; 
				 } 
				 } 
				document.forms[0].elements['wordCount'].value = words.length; 
				if(words.length > maxlimit)
{
					return false;
				}
              }
