function textCounterEval(maxlimit, fieldvalue) {
	var msg = fieldvalue;
	var charnum = msg.length;
	if(charnum >= maxlimit){
		return true;
	} else{
		return false
	}
	}

head.script:
function textCounter(text){
				maxlimit = new Number(4000);
				if(textCounterEval(maxlimit, text)){
					  alert("Input value was over the " + maxlimit + " character limit. You have entered " + text.length + " characters. Please shorten the entry or use the attachments feature in the notes section below.");
					  return false;
				}else{
					return true;
					}
			  }
			  function textCounterPosting(){		  
				msg = new String(document.forms[0].elements['posting'].value);
			    textCounter(msg);
			  }
			  function textCounterCopies(){			  
				msg = new String(document.forms[0].elements['copies'].value);
			    textCounter(msg);
			  }
			  function textCounterAnnouncement(){			  
				msg = new String(document.forms[0].elements['announcement'].value);
				if(msg.length() == 0){
					return false;
				}
			    textCounter(msg);
			  }
			  function textCounterAll(){	  
				msg1 = new String(document.forms[0].elements['posting'].value);
				msg2 = new String(document.forms[0].elements['copies'].value);
				msg3 = new String(document.forms[0].elements['announcement'].value);
				msg4 = new String(document.forms[0].elements['comments'].value);
				if(msg3.length <= 0){
					alert("Field 'Text of Vacancy Announcement' is required and was found to be empty. Please provide.");
					return false;
				}
			    if(textCounter(msg2) && textCounter(msg1) && textCounter(msg3) && textCounter(msg4)){
					return true;
				}else{
					return false;
				}
			  }
